// bubble sort!

module.exports = function(arr) {
	var array_length = arr.length;
	if (array_length < 2) {
		return null;
	}
	while (array_length > 0) {
		for (var i = 1; i < array_length; i++) {
			if (arr[i-1] > arr[i]) {
				var temp = arr[i-1];
				arr[i-1] = arr[i];
				arr[i] = temp;
			}
		}
		array_length--;
	}
}
